mapper.sql:
m.id = #{id}

    and (
    ep.experience_program_title like concat('%', #{search.keyword}, '%')
    or
    co.corp_company_name like concat('%', #{search.keyword}, '%')
    )

mapper.select:
select ep.id, ep.experience_program_title, ep.experience_program_description, ep.experience_program_level, ep.experience_program_recruitment_count, ep.experience_program_work_days, ep.experience_program_work_hours, ep.experience_program_start_date, ep.experience_program_end_date, ep.experience_program_deadline, ep.experience_program_status, ep.experience_program_view_count, ep.experience_program_job, ep.created_datetime, ep.updated_datetime,
ep.corp_id, co.corp_company_name, co.corp_kind_small_id, co.corp_company_type, co.corp_business_number, co.corp_ceo_name, co.corp_kind_id, co.corp_kind_small_id, co.corp_company_size, co.corp_establishment_date, co.corp_website_url, co.corp_fax, co.corp_capital, co.corp_total_sales, co.corp_performance, co.corp_vision
from tbl_experience_program ep join tbl_corp co on ep.corp_id = co.id where ep.id = #{id}
